// Extract company_id from webhook or use default
const company_id = $('Webhook2').item.json.body.company_id || 'default_company';
const career_page_url = $('Webhook2').item.json.body.url_career;

// Check if initial crawl found jobs directly
const result = $input.first().json;

if (result.success && result.total_jobs_found > 0) {
  return {
    has_jobs: true,
    jobs: result.jobs || [],
    source: 'direct_extraction',
    career_page_url: career_page_url,
    company_id: company_id,
    workflow_completed: true
  };
} else {
  // No jobs found, need to crawl child links
  const career_pages = result.career_pages || [];
  return {
    has_jobs: false,
    career_pages: career_pages,
    source: 'need_child_crawling',
    career_page_url: career_page_url,
    company_id: company_id,
    workflow_completed: false
  };
}

// Process jobs found from initial crawl
const jobs = $input.first().json.jobs;
const career_page_url = $input.first().json.career_page_url;
const company_id = $input.first().json.company_id;

const processed_jobs = jobs.map(job => ({
  json: {
    job_name: job.job_name || job.title || 'Unknown Job',
    job_link: job.job_link || job.url || '',
    job_description: job.job_description || job.description || '',
    job_role: job.job_role || job.title || 'Unknown Role',
    job_type: job.job_type || 'Full-time',
    company_id: company_id,
    source_url: career_page_url,
    extraction_method: 'direct_crawl',
    success: true
  }
}));

return processed_jobs;

// Prepare career pages for child crawling
const career_pages = $input.first().json.career_pages;
const career_page_url = $input.first().json.career_page_url;
const company_id = $input.first().json.company_id;

if (!Array.isArray(career_pages) || career_pages.length === 0) {
  return [{
    json: {
      error: 'No career pages found',
      source_url: career_page_url,
      company_id: company_id,
      workflow_completed: true,
      success: false
    }
  }];
}

return career_pages.map(url => ({
  json: {
    career_page: url,
    source_url: career_page_url,
    company_id: company_id
  }
}));

