// Validate AI response and handle errors
const ai_response = $input.first().json;
const current_url = $('Get Job Detail HTML').item.json.url;
const company_id = $('Loop Over Career Pages').item.json.company_id;
const source_url = $('Loop Over Career Pages').item.json.source_url;

// Check if AI response is valid
if (!ai_response || typeof ai_response.hasJob !== 'boolean') {
  return {
    json: {
      error: 'Invalid AI response format',
      current_url: current_url,
      company_id: company_id,
      source_url: source_url,
      success: false,
      continue_loop: true
    }
  };
}

// Check confidence threshold
if (ai_response.confidence < 0.7) {
  return {
    json: {
      error: 'Low confidence job detection',
      confidence: ai_response.confidence,
      reason: ai_response.reason,
      current_url: current_url,
      company_id: company_id,
      source_url: source_url,
      success: false,
      continue_loop: true
    }
  };
}

// Process valid job detection
if (ai_response.hasJob === true) {
  return {
    json: {
      job_name: ai_response.job_name || 'Unknown Job',
      job_link: ai_response.job_link || current_url,
      job_description: ai_response.job_description || '',
      job_role: ai_response.job_role || ai_response.job_name || 'Unknown Role',
      job_type: ai_response.job_type || 'Full-time',
      company_id: company_id,
      source_url: source_url,
      extraction_method: 'ai_detection',
      confidence: ai_response.confidence,
      reason: ai_response.reason,
      success: true
    }
  };
} else {
  // No job found, continue to next link
  return {
    json: {
      continue_loop: true,
      job_found: false,
      current_url: current_url,
      company_id: company_id,
      source_url: source_url,
      reason: ai_response.reason,
      success: true
    }
  };
}

// No child job links found
return {
  json: {
    error: 'No job links found in career page',
    source_url: $('Loop Over Career Pages').item.json.source_url,
    company_id: $('Loop Over Career Pages').item.json.company_id,
    workflow_completed: true,
    success: false
  }
};

// Aggregate all results for comprehensive logging
const all_results = $input.all();
const successful_jobs = all_results.filter(item => 
  item.json.success && item.json.job_name && item.json.job_name !== 'Unknown Job'
);
const failed_extractions = all_results.filter(item => 
  item.json.error || (item.json.success === false)
);
const skipped_pages = all_results.filter(item => 
  item.json.continue_loop && item.json.job_found === false
);

const summary = {
  workflow_name: 'Nhận career url - Optimized',
  timestamp: new Date().toISOString(),
  total_processed: all_results.length,
  successful_jobs: successful_jobs.length,
  failed_extractions: failed_extractions.length,
  skipped_pages: skipped_pages.length,
  success_rate: all_results.length > 0 ? (successful_jobs.length / all_results.length * 100).toFixed(2) + '%' : '0%',

  // Detailed results
  jobs: successful_jobs.map(item => ({
    job_name: item.json.job_name,
    job_role: item.json.job_role,
    job_type: item.json.job_type,
    company_id: item.json.company_id,
    source_url: item.json.source_url,
    extraction_method: item.json.extraction_method,
    confidence: item.json.confidence || 'N/A'
  })),

  errors: failed_extractions.map(item => ({
    error: item.json.error,
    source_url: item.json.source_url,
    company_id: item.json.company_id,
    reason: item.json.reason || 'N/A'
  })),

  skipped: skipped_pages.map(item => ({
    url: item.json.current_url,
    reason: item.json.reason || 'No job detected',
    company_id: item.json.company_id
  })),

  workflow_completed: true,
  version: '2.0-optimized'
};

// Log comprehensive summary
console.log('=== WORKFLOW SUMMARY ===');
console.log(JSON.stringify(summary, null, 2));
console.log('=== END SUMMARY ===');

return summary;
